import { menuItem } from "../utils/menu_items";
const Menu = () => {
  console.log(menuItem);
  return (
    <div className="menu p-0 m-0 d-flex row justify-content-center align-items-center">
      <div className="p-0 m-0 d-flex flex-column justify-content-center align-items-center">
        {menuItem?.map((item) => {
          return (
            <div className="col-md-3 bg-danger">
              <div
                className="img p-0 m-0 w-100"
                style={{ backgroundImage: item.image }}
              >
                {/* <img
                  className="img p-0 m-0 w-100"
                  src={item.image}
                  alt="bonte-item"
                /> */}
              </div>
              <h6>Name : {item.name}</h6>
              <strong>Price : {item.price}</strong>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Menu;
