import { useState } from "react";
import { menuItem } from "../utils/menu_items";
const Menu = () => {
  console.log(menuItem);
  const [menu, setMenu] = useState(menuItem);
  return (
    <div className="menu p-0 m-0 d-flex flex-column justify-content-start align-items-center">
      <div className="p-0 m-0 d-flex row justify-content-center align-items-center bg-danger">
        <ul className="p-0 m-0 d-flex row justify-content-center align-items-center bg-danger">
          <li
            className="col-md-3 col-6 p-0 m-0"
            onClick={() => {
              setMenu(menuItem);
            }}
          >
            All menu
          </li>
          <li
            className="col-md-3 col-2  p-0 m-0"
            onClick={() => {
              setMenu(
                menuItem?.filter(
                  (item) => item.category === "espresso_based_coffee"
                )
              );
            }}
          >
            Espresso based coffee
          </li>
          <li
            className="col-md-3 col-2  p-0 m-0"
            onClick={() => {
              setMenu(
                menuItem?.filter(
                  (item) => item.category === "coldei_based_coffee"
                )
              );
            }}
          >
            Coldei based coffee
          </li>
          <li
            className="col-md-3 col-2  p-0 m-0"
            onClick={() => {
              setMenu(
                menuItem?.filter((item) => item.category === "brewed_coffee")
              );
            }}
          >
            Brewed coffee
          </li>
          <li
            className="col-md-3 col-2  p-0 m-0"
            onClick={() => {
              setMenu(menuItem?.filter((item) => item.category === "shake"));
            }}
          >
            Shake
          </li>
          <li
            className="col-md-3 col-2  p-0 m-0"
            onClick={() => {
              setMenu(menuItem?.filter((item) => item.category === "mocktail"));
            }}
          >
            Mocktail
          </li>
          <li
            className="col-md-3 col-2  p-0 m-0"
            onClick={() => {
              setMenu(menuItem?.filter((item) => item.category === "smoothie"));
            }}
          >
            Smoothie
          </li>
          <li
            className="col-md-3 col-2  p-0 m-0"
            onClick={() => {
              setMenu(
                menuItem?.filter(
                  (item) => item.category === "hottie_without_coffee"
                )
              );
            }}
          >
            Hottie without coffee
          </li>
          <li
            className="col-md-3 col-2  p-0 m-0"
            onClick={() => {
              setMenu(
                menuItem?.filter(
                  (item) => item.category === "coldie_without_coffee"
                )
              );
            }}
          >
            Coldei without coffee
          </li>
        </ul>
      </div>
      <div className="p-3 m-0 d-flex row justify-content-start align-items-center">
        {menu?.map((item) => {
          return (
            <div
              key={item.name}
              className="p-2 m-0 col-md-3 col-sm-6 col-12 rounded"
            >
              <div className="p-0 m-0 ">
                <img
                  className="img-fluid p-0 m-0 w-100"
                  src={item.image}
                  alt="bonte-item"
                />
              </div>
              <div className="item-card p-3 m-0 mt-2 rounded d-flex justify-content-between">
                <div className="p-1 m-0">
                  <label>Name: </label>
                  <h4>{item.name}</h4>
                </div>
                <div className="p-1 m-0">
                  <strong>Price: </strong>
                  <h4>{item.price}</h4>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Menu;
