import { menuItem } from "../utils/menu_items";
const Menu = () => {
  console.log(menuItem);
  return (
    <div className="menu p-0 m-0 d-flex justify-content-center align-items-center">
      <div className="p-0 m-0 d-flex row justify-content-center align-items-center">
        {menuItem?.map((item) => {
          return (
            <div key={item.name} className="item-card col-md-3 col-sm-6 col-10">
              <div
                className="p-0 m-0 "
                style={{ backgroundImage: `url(${item.image})` }}
              >
                {/* <img
                  className="img-fluid p-0 m-0 w-100"
                  src={item.image}
                  alt="bonte-item"
                /> */}
              </div>
              <div>
                <h6>Name : {item.name}</h6>
              </div>
              <div>
                <strong>Price : {item.price}</strong>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Menu;
